import QRCodeStyling from 'qr-code-styling';
import deepmerge from 'deepmerge'

const image = 'data:image/svg+xml;base64,';

export const settings = {
  width: 300,
  height: 300,
  margin: 0,
  qrOptions: { typeNumber: '0', mode: 'Byte', errorCorrectionLevel: 'Q' },
  imageOptions: { hideBackgroundDots: true, imageSize: 0.2, margin: 4 },
  dotsOptions: { type: 'dots', color: '#6000e1', gradient: null },
  backgroundOptions: {
    color: 'white',
    gradient: null,
  },
  image: image,
  dotsOptionsHelper: {
    colorType: { single: true, gradient: false },
    gradient: {
      linear: true,
      radial: false,
      color1: '#6a1a4c',
      color2: '#6a1a4c',
      rotation: '0',
    },
  },
  cornersSquareOptions: { type: 'dot', color: '#6000e1', gradient: null },
  cornersSquareOptionsHelper: {
    colorType: { single: true, gradient: false },
    gradient: {
      linear: true,
      radial: false,
      color1: '#000000',
      color2: '#000000',
      rotation: '0',
    },
  },
  cornersDotOptions: { type: 'dot', color: '#6000e1', gradient: null },
  cornersDotOptionsHelper: {
    colorType: { single: true, gradient: false },
    gradient: {
      linear: true,
      radial: false,
      color1: '#000000',
      color2: '#000000',
      rotation: '0',
    },
  },
  backgroundOptionsHelper: {
    colorType: { single: true, gradient: false },
    gradient: {
      linear: true,
      radial: false,
      color1: '#ffffff',
      color2: '#ffffff',
      rotation: '0',
    },
  },
}

export function create(overrides: any = {}) {
  return new (QRCodeStyling as any)(deepmerge(settings, overrides))
}

export async function generateQRCode(data: string) {
  let qrCode = create({})
  qrCode.update({
    data,
  })
  let blob = await qrCode.getRawData('svg')
  let qrCodeData: string = await new Promise((resolve, reject) => {
    let reader = new FileReader()
    reader.readAsDataURL(blob)
    reader.onloadend = function () {
      resolve(reader.result as any)
    }
  })

  return qrCodeData
}
